import React, { useState } from 'react';
import Header from "../partials/Header"
import Search from "./Search";
import Definition from "./Definition";
import Image from "./Image";
import Pronunce from "./Pronunciation";
import  Save from "./save";
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";

function App() {

  const [text,setText]=useState();

  function handleWord(words){
     setText(words)
  }
  return ( <div>
            <Header />
                <Search onAdd={handleWord}/>
              <div className="container">
                <Definition addText={text}/>
                <Image addImg={text}/>
                <Pronunce addWord={text}/>
                <Router>
                <Switch>
                <Route to="/" exact component={Definition} />
                <Route to="/" exact component={Image} />
                <Route to="/" exact component={Pronunce} />
                <Save  to="bank" component={Save} />
                </Switch>
                </Router>
              </div>
          </div>
          )
}

export default App;
