import React, { useState } from 'react';
import Header from "./partials/Header"
import Search from "./searchpage/Search";
import Definition from "./searchpage/Definition";
import Image from "./searchpage/Image";
import Pronunce from "./searchpage/Pronunciation";
import Bank from "./savepage/Wordbank";
import {Route, BrowserRouter as Router } from "react-router-dom";

function App() {

  const [text,setText]=useState();

  function handleWord(words){
     setText(words)
  }
  return ( <Router>
            <div>
            <Header />
            <Search path="/" onAdd={handleWord} component={Search}/>
              <div className="container">
                <Definition addText={text}/>
                <Image addImg={text}/>
                <Pronunce addWord={text}/>
              </div>
              <Route path="/bank" component={Bank} />
          </div>
          </Router>
          )
}

export default App;
