import React,{useState} from "react";

function Save(){

    const [ownDefinition,setOwnDefinition]=useState();

    function handleChange(event){
        const newDefinition=event.target.value
    setOwnDefinition(newDefinition)
    }

    function handleClick(event){
       console.assert.log(ownDefinition)
        event.preventDefault()
    }



    return <div className="definition">
             <p className="save-head">Characters: 140</p>
             <textarea onChange={handleChange} className="ownDefinition" rows="5" columns="20" maxLength="140" placeholder="Type the definition in your own words here or copy and paste from below. Limit 140 characters."></textarea>
             <button onClick={handleClick} className="save-term" >Save to your  Word &#8226; Bank</button>
            </div>
}

export default Save;
