import React, { useState } from 'react';
import Header from "./search/Header"
import Search from "./search/Search";
import Definition from "./search/Definition";
import Image from "./search/Image";
import Pronunce from "./search/Pronunciation";
import  Save from "./search/save";

function AllSearchPages(){
    const [text,setText]=useState();
    
  function handleWord(words){
     setText(words)
  }

    return <div>
                <Header />
                    <Search onAdd={handleWord}/>
                  <div className="container">
                    <Definition addText={text}/>
                    <Image addImg={text}/>
                    <Pronunce addWord={text}/>
                    <Save />
                  </div>        
            </div>
}

export default AllSearchPages;
