import React,{useState} from "react";

function Save(){
    
    // $("#ownDefinition").keyup(function() {
    //     var text_length = $("#ownDefinition").val().length;
    //     var text_remaining = text_max - text_length;
    //     $('#textarea_feedback').html("Characters remaining: " + text_remaining);
    // });

    const [num,setNum]=useState();

    function handleKeyUp(event){
        var text_max = 140;
        var text_length= event.key;
        var text_remaining = text_max-text_length
        setNum(text_remaining)
    }

    return <div className="definition">
             <p className="save-head">Characters remaining: {num}</p>
             <textarea onKeyUp={handleKeyUp} className="ownDefinition" rows="5" columns="20" maxLength="140" placeholder="Type the definition in your own words here or copy and paste from below. Limit 140 characters."></textarea>
             <button className="save-term" >Save to your Word &#8226; Bank</button>
            </div>
}

export default Save;
